mapper.select:
select st.id id,st.name siteName,s.time,isnull(round(s.value,0),2) value
from site_factor_data_minute s
left join device_factor d on s.factor_code = d.factor_code and d.is_deleted = 0
left join factor f on f.id = d.factor_id and f.is_deleted = 0
left join site st on st.id = s.site_id and st.is_deleted = 0
where s.is_deleted = 0
and f.device_type = 2
and st.id = #{queryDTO.id}

    and s.time >= #{queryDTO.start}


    and s.time <= #{queryDTO.end}

select site.id, site.name, site.longitude, site.latitude, site.record_level historyLevel, site.record_time historyLevelTime, isnull(round(t.value,2),0) value, t.time time, factorId from site inner join (select * from ( select st.id id,st.name,s.value value,s.time,f.device_type,f.id factorId from site st left join site_factor_data_hour s on st.id = s.site_id and s.is_deleted = 0 left join device_factor d on s.factor_code = d.factor_code and d.is_deleted = 0 left join factor f on f.id = d.factor_id and f.is_deleted = 0 where st.is_deleted = 0
and s.time >= #{queryDTO.start}
and s.time <= #{queryDTO.end}
) temp where temp.device_type = 1) t on site.id = t.id where site.is_deleted = 0 and site.id = #{queryDTO.id}
