mapper.select:
select isnull(round(sum(s.value), 2), 0)
from site_factor_data_minute s
         left join device_factor d on s.factor_code = d.factor_code and d.is_deleted = 0
         left join factor f on f.id = d.factor_id and f.is_deleted = 0
         left join site st on st.id = s.site_id and st.is_deleted = 0
where s.is_deleted = 0
  and f.device_type = 2
  and s.site_id = #{siteId}
  and s.time >= #{start}
  and s.time <= #{end}

select s.id id,s.name siteName,s.longitude longitude,s.latitude latitude,t.factorCode factorCode,isnull(round(t.value,2),0) value from site s left join( select st.id id,s.value value,d.factor_code factorCode from site_factor_data_minute s left join device_factor d on s.factor_code = d.factor_code and d.is_deleted = 0 left join factor f on f.id = d.factor_id and f.is_deleted = 0 left join site st on st.id = s.site_id and st.is_deleted = 0 where s.is_deleted = 0 and f.device_type = 2
and s.time >= #{queryDTO.start}
and s.time <= #{queryDTO.end} ) t on t.id = s.id where s.is_deleted = 0 and s.id in (select site.id from site inner join device on site.id = device.site_id and device.is_deleted = 0 inner join device_factor df on df.device_id = device.id and df.is_deleted = 0 where site.is_deleted = 0 and df.factor_id = 3 )

select st.id id,st.name siteName,s.time time,isnull(round(s.value,2),0) value
from site_factor_data_minute s
left join device_factor d on s.factor_code = d.factor_code and d.is_deleted = 0
left join factor f on f.id = d.factor_id and f.is_deleted = 0
left join site st on st.id = s.site_id and st.is_deleted = 0
where s.is_deleted = 0
and f.device_type = 2
and st.id = #{queryDTO.id}

    and s.time >= #{queryDTO.start}


    and s.time <= #{queryDTO.end}

select site.id id, site.name siteName, site.longitude longitude, site.latitude latitude, isnull(round(t.value,2),0) value, t.factorId factorId, t.factorCode factorCode, site.record_level historyLevel, site.record_time historyLevelTime from site left join (select * from ( select st.id id,st.name,s.value value,f.device_type,f.id factorId,d.factor_code factorCode from site st left join site_factor_data_minute s on st.id = s.site_id and s.is_deleted = 0 left join device_factor d on s.factor_code = d.factor_code and d.is_deleted = 0 left join factor f on f.id = d.factor_id and f.is_deleted = 0 where st.is_deleted = 0
and s.time = #{queryDTO.time}
) temp where temp.device_type = 1) t on site.id = t.id where site.is_deleted = 0 and site.id in (select site.id from site inner join device on site.id = device.site_id and device.is_deleted = 0 inner join device_factor df on df.device_id = device.id and df.is_deleted = 0 where site.is_deleted = 0 and df.factor_id in (1,2) group by site.id )

select site.id, site.name, site.longitude, site.latitude, site.record_level historyLevel, site.record_time historyLevelTime, isnull(round(t.value,2),0) value, t.time time, factorId from site inner join (select * from ( select st.id id,st.name,s.value value,s.time,f.device_type,f.id factorId from site st left join site_factor_data_minute s on st.id = s.site_id and s.is_deleted = 0 left join device_factor d on s.factor_code = d.factor_code and d.is_deleted = 0 left join factor f on f.id = d.factor_id and f.is_deleted = 0 where st.is_deleted = 0
and s.time >= #{queryDTO.start}
and s.time <= #{queryDTO.end}
) temp where temp.device_type = 1) t on site.id = t.id where site.is_deleted = 0 and site.id = #{queryDTO.id}
